import React, { useEffect, useState } from 'react'
import { Row, Col } from 'antd';
import 'antd/dist/antd.css';
import './DetailPokemon.css'
import { callApidata } from '../../api/callAPI';
import {Link} from 'react-router-dom'
import { ListEvolution } from './DPstore';
import { observer } from 'mobx-react'

function Detail(props){
    function habitat(habitat){
        if (habitat!==undefined){
            console.log(habitat)
            return (
                <Col span={12}><Link to={'/habitat/'}>habitat : </Link></Col>
            )
        }
    }
    function eve(string){
        callApidata(string,'pokemon-species').then(res=>{
            if (res.data.evolves_from_species!==null){
                let name=res.data.evolves_from_species.name
                callApidata(name,'pokemon').then(res=>{
                    ListEvolution.addTodo({name:res.data.name,img:res.data.sprites.front_default})
                    eve(res.data.name)
                })
            }
        })
    }
    function show(list){
        let result=null
        if (list.length>0){
            result=list.map(value=>{
                return(
                    <li>
                        <p>{value.name}</p>
                        <img src={value.img}></img>
                    </li>
                )
            })
        }
        return result
    }
    useEffect(()=>{
        callApidata(props.match.params.id,'pokemon').then(res=>{
            setIF(res.data)
            callApidata(res.data.id+1,'pokemon').then(res=>{
                setN(res.data.name)
            })
            callApidata(res.data.id-1,'pokemon').then(res=>{
                setP(res.data.name)
            })
            ListEvolution.changelist([])
            ListEvolution.addTodo({name:res.data.name,img:res.data.sprites.front_default})
            eve(res.data.name)
        })
        callApidata(props.match.params.id,'pokemon-species').then(res=>{
            setD(res.data)
        })
    },[props])
    const [inforPokemon,setIF]=useState({})
    const [detail,setD]=useState({})
    const [next,setN]=useState('')
    const [prev,setP]=useState('')
    if (inforPokemon.sprites!== undefined ){
        return(

            <Row  className="detail">
                <Col span={6} ></Col>
                <Col span={12}>
                        <Link to={'/pokemon/'+prev}><button>prev</button></Link>    
                        <Link to={'/pokemon/'+next}><button>next</button></Link> 
                    <p>{inforPokemon.name}</p>
                    <Row gutter={[16, 16]}>
                        <Col span={12} > 
                            <img className="gray" src={inforPokemon.sprites.front_default} alt="abc"></img>
                        </Col>
                        <Col span={12}> 
                            { ()=>{
                                if (detail.flavor_text_entries!==undefined)
                                return(
                                    <p>{detail.flavor_text_entries[0].flavor_text}</p>
                                )
                            } }
                            <Row className="inforbasic" >
                                <Col span={12}>height : {inforPokemon.height}</Col>
                                <Col span={12}>width : {inforPokemon.weight}</Col>
                                <Col span={12}>experience : {inforPokemon.base_experience}</Col>
                                {habitat(detail.habitat)}
                            </Row>
                            <p>abilities :</p>
                            <Row >
                            {inforPokemon.abilities.map((values,index)=>
                                <Col span={11} className="abilities">
                                    <Link to={'/ability/'+values.ability.name}>{values.ability.name}</Link>
                                </Col>
                            )}
                            </Row>
                            <p>types :  </p>
                            <Row>
                            {inforPokemon.types.map((values,index)=>
                                <Col span={11} className="abilities">
                                    <Link to={'/type/'+values.type.name}>{values.type.name}</Link>
                                </Col>
                            )}
                            </Row>
                        </Col>
                    </Row>
                    <p><b>Evolutions</b></p>
                    <ul>
                    {show(ListEvolution.shop)}
                    </ul>
                </Col>
                <Col span={6} ></Col>
            </Row>
        )
    } else {
        return(
            <div></div>
        )
    }
    
}
export default observer(Detail)
