import React, { useEffect, useState } from 'react'
import { Row, Col } from 'antd';
import 'antd/dist/antd.css';
import './DetailPokemon.css'
import { callApidata } from '../../api/callAPI';
import {Link} from 'react-router-dom'

function Detail(){
    useEffect(()=>{
        // callApidata('105','pokemon').then(res=>{
        //     setIF(res.data)
        // })
        callApidata('105','pokemon-species').then(res=>{
            setD(res.data)
        })
    },[])
    const [inforPokemon,setIF]=useState({})
    const [detail,setD]=useState({})
    console.log(inforPokemon)
    if (inforPokemon.sprites!== undefined){
        return(

            <Row  className="detail">
                <Col span={6} ></Col>
                <Col span={12}>
                    <p>{inforPokemon.name}</p>
                    <Row gutter={[16, 16]}>
                        <Col span={12} > 
                            <img className="gray" src={inforPokemon.sprites.back_default} alt="abc"></img>
                        </Col>
                        <Col span={12}> 
                            <Row className="inforbasic" >
                                <Col span={12}>chiều cao : {inforPokemon.height}</Col>
                                <Col span={12}>chiều rộng : {inforPokemon.weight}</Col>
                                <Col span={12}>kinh nghiệm : {inforPokemon.base_experience}</Col>
                                <col span={12}>môi trường : {detail.habitat}</col>
                            </Row>
                            <p>Kỹ Năng :</p>
                            <Row gutter={[16, 16]}>
                            {inforPokemon.abilities.map((values,index)=>
                                <Col span={12} className="abilities">
                                    <Link to='/'>{values.ability.name}</Link>
                                </Col>
                            )}
                            </Row>
                            
                            <p>hệ :  </p>
                            {inforPokemon.types.map((values,index)=>
                                <div key={index}> 
                                    <p> {values.type.name}</p>
                                    <p>{values.type.url}</p>
                                </div>
                                
                            )}
                        </Col>
                    </Row>
                </Col>
                <Col span={6} ></Col>
            </Row>
        )
    } else {
        return(
            <div></div>
        )
    }
    
}
export default Detail
