import React, { useState, useEffect } from 'react'
import Axios from 'axios'
import { observer } from 'mobx-react'
import { listinf , inf} from './inforbasic'
import { callApidata } from '../../api/callAPI'
import { show } from './show'
import { Row, Col } from 'antd'
function ShowPokemon(props){

    console.log(props)
    useEffect(()=>{
        if (props.type==='hot'){
            
        } else {
            callApidata(props.match.params.id,props.type).then(res=>{
                listinf.changelist([])
                res.data.pokemon.forEach(async (value) => {
                    Axios.get(value.pokemon.url).then(res=>{
                        let img=res.data.sprites.front_default || ''
                        listinf.addTodo({id:res.data.id,img:img,name:res.data.name})
                    })
                    
                });
            })
        }
    },[props])
    console.log(listinf.list.length)
    return(
        <Row> 
            <Col span={5}></Col>
            <Col span={14}>
                <Row>
                    {show(listinf.shop)}
                </Row>
                </Col>
            <Col></Col>
        </Row>
    )
}
export default observer(ShowPokemon)
