import Axios from 'axios'
import { listinf } from './inforbasic'
import { callApidata } from '../../api/callAPI'
import { show } from './show'
function ShowPokemon(){
    useEffect(()=>{
        Axios.get('/hot').then(res=>{
            console.log(res.data)
            setLid(res.data)
            listinf.changelist([])
            res.data.forEach(async (value) => {
                callApidata(value.id,'pokemon').then(res=>{
                    listinf.addTodo({id:res.data.id,img:res.data.sprites.front_default})
                    console.log(listinf)
                })
            });
        })

    },[])
    console.log(listinf.list.length)
