import React, { useEffect, useState } from 'react'
import { Row, Col } from 'antd';
import 'antd/dist/antd.css';
import './DetailPokemon.css'
import { callApidata } from '../../api/callAPI';
import {Link} from 'react-router-dom'

function Detail(){
    function cut(string){
        string=string.slice(0,string.length-1)
        let index=string.lastIndexOf('/')
        return string.slice(index+1,string.length)
    }
    useEffect(()=>{
        callApidata('3','pokemon').then(res=>{
            setIF(res.data)
        })
        callApidata('3','pokemon-species').then(res=>{
            setD(res.data)
            console.log(res.data.habitat.name)
        })
    },[])
    const [inforPokemon,setIF]=useState({})
    const [detail,setD]=useState({})
    if (inforPokemon.sprites!== undefined && detail.habitat!== undefined){
        return(

            <Row  className="detail">
                <Col span={6} ></Col>
                <Col span={12}>
                    <p>{inforPokemon.name}</p>
                    <Row gutter={[16, 16]}>
                        <Col span={12} > 
                            <img className="gray" src={inforPokemon.sprites.front_default} alt="abc"></img>
                        </Col>
                        <Col span={12}> 
                            <Row className="inforbasic" >
                                <Col span={12}>height : {inforPokemon.height}</Col>
                                <Col span={12}>width : {inforPokemon.weight}</Col>
                                <Col span={12}>experience : {inforPokemon.base_experience}</Col>
                                <Col span={12}><Link to={'/habitat/'+cut(detail.habitat.url)}>habitat : {detail.habitat.name}</Link></Col>
                            </Row>
                            <p>abilities :</p>
                            <Row >
                            {inforPokemon.abilities.map((values,index)=>
                                <Col span={11} className="abilities">
                                    <Link to={'/ability/'+cut(values.ability.url)}>{values.ability.name}</Link>
                                </Col>
                            )}
                            </Row>
                            
                            <p>types :  </p>
                            <Row>
                            {inforPokemon.types.map((values,index)=>
                                <Col span={11} className="abilities">
                                    <Link to='/'>{values.type.name}</Link>
                                </Col>
                                
                            )}
                            </Row>
                        </Col>
                    </Row>
                </Col>
                <Col span={6} ></Col>
            </Row>
        )
    } else {
        return(
            <div></div>
        )
    }
    
}
export default Detail
