import { types } from "mobx-state-tree";



const Evolution=types.model({
    name:types.string,
    img:types.optional(types.string,'')
})
const Evolutions=types.model({
    list:types.optional(types.array(Evolution),[]),
    listWeaknesses:types.optional(types.array(types.string),[])
}).views((self) => ({
    get shop() {
        return self.list.reverse()
    }
})).actions( (self) => {
    function addTodo(Evolution){
        self.list.push(Evolution)
        // console.log(self.list)
    }
    function addWeaknesses(data){
        self.listWeaknesses.push(data)
        // console.log(self.list)
    }
    function  changelist(newList){
        self.list=newList
    }
    return {
        addTodo,
        changelist,
        addWeaknesses
    }
})

// export const ListWeaknesses=Weaknesses.create()
export const ListEvolution=Evolutions.create()
export const pokemon=pokemon1.create()
