import axios from "axios"
export async function callApidata(value,type) {
    const data =await axios.get(`https://pokeapi.co/api/v2/${type}/${value}`)
    return data
  }
export async function callApi(value,type) {
    const data =await axios.get(`https://pokeapi.co/api/v2/${type}/${value}`).then(res=>res.data)
    return data
  }
