import React, { useState, useEffect } from 'react'
import Axios from 'axios'
import { observer } from 'mobx-react'
import { listinf , inf} from './inforbasic'
import { callApidata } from '../../api/callAPI'
import { show } from './show'
import { Row, Col } from 'antd'
function ShowPokemon(props){

    console.log(props)
    useEffect(()=>{
        if (props.type==='hot'){
            Axios.get('/hot').then(res=>{
                listinf.changelist([])
                setLid(res.data)
                res.data.forEach(async (value) => {
                    await callApidata(value.id,'pokemon').then(res=>{
                        console.log(value.id)
                        listinf.addTodo({id:res.data.id,img:res.data.sprites.front_default,name:res.data.name})
                    })
                });
            })
        } else {
            callApidata
        }
    },[props])
    const [listid,setLid]=useState([])
    return(
        <Row> 
            <Col span={5}></Col>
            <Col span={14}>
                <Row>
                    {show(listinf.shop)}
                </Row>
                </Col>
            <Col></Col>
        </Row>
    )
}
export default observer(ShowPokemon)
