import React, { useState, useEffect } from 'react'
import Axios from 'axios'
import { observer } from 'mobx-react'
import { listinf , inf} from './inforbasic'
import { callApidata } from '../../api/callAPI'
import { show } from './show'
import { observable } from 'mobx'
function ShowPokemon(){

    useEffect(()=>{
        Axios.get('/hot').then(res=>{
            listinf.changelist([])
            
            setLid(res.data)

            res.data.forEach(async (value) => {
                callApidata(value.id,'pokemon').then(res=>{
                    listinf.addTodo({id:res.data.id,img:res.data.sprites.front_default})
                })
            });
        })
        
    },[])
    console.log(listinf.list.length)
    const [listid,setLid]=useState([])
    return(
        <div>
            <show list={listinf.shop}/>
        </div>
    )
}
export default observer(ShowPokemon)
